/* eslint-disable no-console */
import { exec } from "node:child_process";
import { exit } from "node:process";
import { promisify } from "node:util";

import read from "read-file-utf8";
import write from "write-file-utf8";

import { getContractNameArgument } from "./_contracts.js";
import { getContractDeployInfoPathname } from "./_local-blockchain.js";

const run = promisify(exec);

try {
  const contractName = getContractNameArgument();

  // Read private key from anvil config which is generated by `npm run local-blockchain`.

  const anvilConfigPathname = "local-blockchain/anvil-config.json";
  const anvilConfig = await read(anvilConfigPathname);

  const privateKey = anvilConfig.private_keys[0];
  if (!privateKey)
    throw new Error(`Cannot get private key from ${anvilConfigPathname}`);

  // Run command to deploy contract, notice the `--json` flag.

  const command = `forge create contracts/src/${contractName}.sol:${contractName} --rpc-url http://localhost:8545 --private-key ${privateKey} --json`;
  console.info(command);

  const { stdout } = await run(command);

  // Write the command output to related JSON file.

  await write(getContractDeployInfoPathname(contractName), stdout);
} catch (error) {
  console.error(error);
  exit(1);
}
